export class AdminArtistAddComponent {
    constructor(http, uploadService, categoriesService, artistService, productService, route, toastService) {
        this.http = http;
        this.uploadService = uploadService;
        this.categoriesService = categoriesService;
        this.artistService = artistService;
        this.productService = productService;
        this.route = route;
        this.toastService = toastService;
        this.uploadSuccess = false;
        this.addSuccess = false;
        this.aristForm = new FormGroup({
            name: new FormControl('', [Validators.required, Validators.minLength(3)]),
            email: new FormControl('', [Validators.required, Validators.email]),
            country: new FormControl('', [
                Validators.required,
                Validators.minLength(3),
            ]),
            gender: new FormControl('', Validators.required),
            date: new FormControl('', Validators.required),
            department: new FormControl('', Validators.required),
            avatar: new FormControl('', Validators.required),
        });
        this.version = VERSION;
        this.categeories = this.categoriesService
            .getcategoriess()
            .subscribe(async (data) => {
            this.categeories = await data;
        });
    }
    get name() {
        return this.aristForm.get('name');
    }
    get email() {
        return this.aristForm.get('email');
    }
    get country() {
        return this.aristForm.get('country');
    }
    get gender() {
        return this.aristForm.get('gender');
    }
    get date() {
        return this.aristForm.get('date');
    }
    get department() {
        return this.aristForm.get('department');
    }
    addArtist() {
        console.log(this.aristForm.value);
        console.log(this.selectedFiles);
        this.uploadService
            .upload('images/arists', this.selectedFiles[0].name, this.selectedFiles[0])
            .then((data) => {
            if (data &&
                this.selectedFiles &&
                this.uploadSuccess &&
                this.aristForm.valid) {
                this.artistService
                    .addArtist({
                    name: this.name.value,
                    dateOfBirth: this.date.value,
                    country: this.country.value,
                    email: this.email.value,
                    gender: this.gender.value,
                    department: this.department.value,
                    avatar: data,
                    TimeStamp: {
                        createdAt: new Date().toDateString(),
                        updatedAt: new Date().toDateString(),
                        removedAt: null,
                    },
                })
                    .then(() => {
                    this.addSuccess = true;
                    setTimeout(() => {
                        this.route.navigate(['/admin/artists']);
                    }, 1000);
                });
            }
        });
    }
    upload(files) {
        this.uploadAndProgress(files);
        this.selectedFiles = files;
    }
    uploadAndProgress(files) {
        var formData = new FormData();
        Array.from(files).forEach((f) => formData.append('file', f));
        this.http
            .post('https://file.io', formData, {
            reportProgress: true,
            observe: 'events',
        })
            .subscribe((event) => {
            if (event.type === HttpEventType.UploadProgress) {
                this.percentDone = Math.round((100 * event.loaded) / event.total);
                console.log(files);
            }
            else if (event instanceof HttpResponse) {
                this.uploadSuccess = true;
            }
        });
    }
    ngOnInit() { }
    showSuccess() {
        this.toastService.show('Add success wait..', {
            classname: 'bg-success text-light',
            delay: 1000,
        });
