export class AdminCustomizeOrderAllComponent {
    constructor(prdService, categoryservice, customorderService, userService) {
        this.prdService = prdService;
        this.categoryservice = categoryservice;
        this.customorderService = customorderService;
        this.userService = userService;
        this.displayedColumns = [
            'image',
            'name',
            'price',
            'create',
            'update',
            'action',
        ];
        this.products = [];
        this.prdService.getProducts().subscribe(async (data) => {
            this.dataSource = new MatTableDataSource(data);
            console.log(this.dataSource);
        });
        this.customorderService.getCustoms().subscribe(async (data) => {
            let arr = [];
            console.log('custome', data);
            await data.map((element) => {
                this.prdService.getOneProduct(element.productId).subscribe((prd) => {
                    console.log('prd', prd);
                    this.userService.getOneUser(element.userId).subscribe((user) => {
                        console.log('user', user[0]);
                        arr.push(Object.assign(Object.assign({}, data), { prd: {
                                data: prd,
                            }, user: {
                                data: user,
                            } }));
                    });
                });
            });
            console.log('arr', arr);
        });
    }
    ngAfterViewInit() {
        this.dataSource.paginator = this.paginator;
        this.dataSource.sort = this.sort;
    }
    ngOnInit() { }
    applyFilter(filterValue) {
        filterValue = filterValue.trim(); // Remove whitespace
        filterValue = filterValue.toLowerCase(); // Datasource defaults to lowercase matches
        this.dataSource.filter = filterValue;
    }
    remove(id) {
        this.prdService.removeProduct(id);
