        console.log(this.selectedFiles);
        this.uploadService
            .upload('images/services', this.selectedFiles[0].name, this.selectedFiles[0])
            .then((data) => {
            if (data &&
                this.selectedFiles &&
                this.uploadSuccess &&
                this.service.valid) {
                this.productService
                    .addProduct({
                    title: this.title.value,
                    description: this.description.value,
                    size: this.size.value ? this.size.value : '',
                    price: this.price.value,
                    images: {
                        mainImage: data,
                        subImages: [data, data, data],
                    },
                    artistID: this.artist.value,
                    categoryID: this.category.value,
                    TimeStamp: {
                        createdAt: new Date().toDateString(),
                        updatedAt: new Date().toDateString(),
                        removedAt: null,
                    },
                })
                    .then(() => {
                    this.addSuccess = true;
                    this.route.navigate(['/admin/services']);
                    // setTimeout(() => {
                    //   this.route.navigate(['/admin/services']);
                    // }, 500);
                });
            }
        });
    }
    upload(files) {
        this.uploadAndProgress(files);
        this.selectedFiles = files;
    }
    uploadAndProgress(files) {
        var formData = new FormData();
        Array.from(files).forEach((f) => formData.append('file', f));
        this.http
            .post('https://file.io', formData, {
            reportProgress: true,
            observe: 'events',
        })
            .subscribe((event) => {
            if (event.type === HttpEventType.UploadProgress) {
                this.percentDone = Math.round((100 * event.loaded) / event.total);
                console.log(files);
            }
            else if (event instanceof HttpResponse) {
                this.uploadSuccess = true;
            }
        });
    }
