import { __decorate } from "tslib";
import { Component, ViewChild } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
let AdminCategeoriesAllComponent = class AdminCategeoriesAllComponent {
    constructor(categeoryService, toastService) {
        this.categeoryService = categeoryService;
        this.toastService = toastService;
        this.displayedColumns = [
            'image',
            'name',
            'created',
            'updated',
            'action',
        ];
        this.categeoryService.getcategoriess().subscribe(async (data) => {
            this.dataSource = await new MatTableDataSource(data);
            console.log(data);
        });
    }
    ngAfterViewInit() {
        this.dataSource.paginator = this.paginator;
        this.dataSource.sort = this.sort;
    }
    ngOnInit() { }
    applyFilter(filterValue) {
        filterValue = filterValue.trim(); // Remove whitespace
        filterValue = filterValue.toLowerCase(); // Datasource defaults to lowercase matches
        this.dataSource.filter = filterValue;
    }
    remove(id) {
        this.categeoryService.removeCategory(id);
    }
};
